import React from 'react'
import {Link} from 'react-router'

import './index.css'

const Header = () => (
    <div className="page-header">
        <nav className="navbar navbar-default">
            <div className="container-fluid">
                <div className="navbar-header">
                <button type="button" className="navbar-toggle collapsed" data-toggle="collapse" data-target="#navbar-main">
                    <span className="icon-bar"></span>
                    <span className="icon-bar"></span>
                    <span className="icon-bar"></span>
                </button>
                <Link to='/' className="navbar-brand" href="#">Главная</Link>
                </div>
                <div className="collapse navbar-collapse" id="navbar-main">
                    <ul className="nav navbar-nav">
                        <li>
                            <Link to='/news'>
                                Новости
                            </Link>
                        </li>
                        <li>
                            <Link to='/workout-plans'>
                                Планы тренировок
                            </Link>
                        </li>
                        <li>
                            <a href="https://drive.google.com/file/d/14ctAVGZgDE3iZjKSWfWKQtXcnW1ZCWTV/view" target="_blank">Таблицы Мороза</a>
                        </li>
                        <li>
                            <a href="/uploads/docs/2019-kalendar.pdf" target="_blank">Календарь соревнований</a>
                        </li>
                        <li>
                            <a href="/uploads/docs/NORMATIVI-SGSU1.pdf" target="_blank">Нормативы</a>
                        </li>
                        <li>
                            <Link to='/contacts'>
                                Контакты
                            </Link>
                        </li>
                    </ul>
                    <ul className="nav navbar-nav navbar-right">
                        <li>
                            <Link to='/basket'>
                                Корзина
                            </Link>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    </div>
) 

export default Header;