import React from 'react'

import Header from 'components/Header'

const WorkoutPlans = () => (
    <div>
        <Header />
        <div className="container">
            <h2 style={{textAlign: 'center'}}>Планы тренировок</h2>
            <div className="row">
                <div className="col-xs-12">
                    <div>Тренировки с гирями по 8 кг</div>
                    <img src="/images/workout-plans/1.jpg" />
                </div>
                <div className="col-xs-12">
                    <div>Тренировки с гирями по 16 кг</div>
                    <img src="/images/workout-plans/2.jpg" />
                </div>
                <div className="col-xs-12">
                    <div>Тренировки с гирями по 24 кг</div>
                    <img src="/images/workout-plans/3.jpg" />
                </div>
                <div className="col-xs-12">
                    <div>Тренировки с гирями по 32 кг</div>
                    <img src="/images/workout-plans/4.jpg" />
                </div>
            </div>
        </div>
    </div>
)

export default WorkoutPlans